<?php
 namespace Illuminate\Console\Scheduling; use Illuminate\Console\Command; class ScheduleRunCommand extends Command { protected $name = 'schedule:run'; protected $description = 'Run the scheduled commands'; protected $schedule; public function __construct(Schedule $schedule) { $this->schedule = $schedule; parent::__construct(); } public function handle() { $eventsRan = false; foreach ($this->schedule->dueEvents($this->laravel) as $event) { if (! $event->filtersPass($this->laravel)) { continue; } $this->line('<info>Running scheduled command:</info> '.$event->getSummaryForDisplay()); $event->run($this->laravel); $eventsRan = true; } if (! $eventsRan) { $this->info('No scheduled commands are ready to run.'); } } } 