<?php
 namespace Illuminate\Contracts\Logging; interface Log { public function alert($message, array $context = []); public function critical($message, array $context = []); public function error($message, array $context = []); public function warning($message, array $context = []); public function notice($message, array $context = []); public function info($message, array $context = []); public function debug($message, array $context = []); public function log($level, $message, array $context = []); public function useFiles($path, $level = 'debug'); public function useDailyFiles($path, $days = 0, $level = 'debug'); } 