<?php
 namespace Illuminate\Routing; class PendingResourceRegistration { protected $registrar; protected $name; protected $controller; protected $options = []; public function __construct(ResourceRegistrar $registrar, $name, $controller, array $options) { $this->name = $name; $this->options = $options; $this->registrar = $registrar; $this->controller = $controller; } public function only($methods) { $this->options['only'] = is_array($methods) ? $methods : func_get_args(); return $this; } public function except($methods) { $this->options['except'] = is_array($methods) ? $methods : func_get_args(); return $this; } public function names($names) { $this->options['names'] = $names; return $this; } public function name($method, $name) { $this->options['names'][$method] = $name; return $this; } public function parameters($parameters) { $this->options['parameters'] = $parameters; return $this; } public function parameter($previous, $new) { $this->options['parameters'][$previous] = $new; return $this; } public function middleware($middleware) { $this->options['middleware'] = $middleware; return $this; } public function __destruct() { $this->registrar->register($this->name, $this->controller, $this->options); } } 