<?php
 namespace Illuminate\Validation; use Illuminate\Support\Traits\Macroable; class Rule { use Macroable; public static function dimensions(array $constraints = []) { return new Rules\Dimensions($constraints); } public static function exists($table, $column = 'NULL') { return new Rules\Exists($table, $column); } public static function in($values) { return new Rules\In(is_array($values) ? $values : func_get_args()); } public static function notIn($values) { return new Rules\NotIn(is_array($values) ? $values : func_get_args()); } public static function unique($table, $column = 'NULL') { return new Rules\Unique($table, $column); } } 