<?php
 namespace League\OAuth1\Client\Server; class User implements \IteratorAggregate { public $uid = null; public $nickname = null; public $name = null; public $firstName = null; public $lastName = null; public $email = null; public $location = null; public $description = null; public $imageUrl = null; public $urls = array(); public $extra = array(); public function __set($key, $value) { if (isset($this->{$key})) { $this->{$key} = $value; } } public function __get($key) { if (isset($this->{$key})) { return $this->{$key}; } } public function getIterator() { return new \ArrayIterator($this); } } 