 class Swift_StreamFilters_StringReplacementFilterFactory implements Swift_ReplacementFilterFactory { private $filters = array(); public function createFilter($search, $replace) { if (!isset($this->filters[$search][$replace])) { if (!isset($this->filters[$search])) { $this->filters[$search] = array(); } if (!isset($this->filters[$search][$replace])) { $this->filters[$search][$replace] = array(); } $this->filters[$search][$replace] = new Swift_StreamFilters_StringReplacementFilter($search, $replace); } return $this->filters[$search][$replace]; } } 