<?php
 namespace Symfony\Component\HttpKernel\CacheClearer; class ChainCacheClearer implements CacheClearerInterface { protected $clearers; public function __construct($clearers = array()) { $this->clearers = $clearers; } public function clear($cacheDir) { foreach ($this->clearers as $clearer) { $clearer->clear($cacheDir); } } public function add(CacheClearerInterface $clearer) { @trigger_error(sprintf('The "%s()" method is deprecated since Symfony 3.4 and will be removed in 4.0, inject the list of clearers as a constructor argument instead.', __METHOD__), E_USER_DEPRECATED); $this->clearers[] = $clearer; } } 