<?php
 namespace Symfony\Component\HttpKernel\Config; use Symfony\Component\Config\Resource\SelfCheckingResourceInterface; class EnvParametersResource implements SelfCheckingResourceInterface, \Serializable { private $prefix; private $variables; public function __construct($prefix) { $this->prefix = $prefix; $this->variables = $this->findVariables(); } public function __toString() { return serialize($this->getResource()); } public function getResource() { return array('prefix' => $this->prefix, 'variables' => $this->variables); } public function isFresh($timestamp) { return $this->findVariables() === $this->variables; } public function serialize() { return serialize(array('prefix' => $this->prefix, 'variables' => $this->variables)); } public function unserialize($serialized) { if (\PHP_VERSION_ID >= 70000) { $unserialized = unserialize($serialized, array('allowed_classes' => false)); } else { $unserialized = unserialize($serialized); } $this->prefix = $unserialized['prefix']; $this->variables = $unserialized['variables']; } private function findVariables() { $variables = array(); foreach ($_SERVER as $key => $value) { if (0 === strpos($key, $this->prefix)) { $variables[$key] = $value; } } ksort($variables); return $variables; } } 