 namespace Symfony\Component\HttpKernel\DependencyInjection; use Symfony\Component\DependencyInjection\Extension\Extension as BaseExtension; abstract class Extension extends BaseExtension { private $classes = array(); private $annotatedClasses = array(); public function getClassesToCompile() { if (\PHP_VERSION_ID >= 70000) { @trigger_error(__METHOD__.'() is deprecated since Symfony 3.3, to be removed in 4.0.', E_USER_DEPRECATED); } return $this->classes; } public function getAnnotatedClassesToCompile() { return $this->annotatedClasses; } public function addClassesToCompile(array $classes) { if (\PHP_VERSION_ID >= 70000) { @trigger_error(__METHOD__.'() is deprecated since Symfony 3.3, to be removed in 4.0.', E_USER_DEPRECATED); } $this->classes = array_merge($this->classes, $classes); } public function addAnnotatedClassesToCompile(array $annotatedClasses) { $this->annotatedClasses = array_merge($this->annotatedClasses, $annotatedClasses); } } 