<?php
 namespace Symfony\Component\Routing\Loader\Configurator\Traits; use Symfony\Component\Routing\Loader\Configurator\RouteConfigurator; use Symfony\Component\Routing\Route; use Symfony\Component\Routing\RouteCollection; trait AddTrait { private $collection; private $name = ''; final public function add($name, $path) { $parentConfigurator = $this instanceof RouteConfigurator ? $this->parentConfigurator : null; $this->collection->add($this->name.$name, $route = new Route($path)); return new RouteConfigurator($this->collection, $route, '', $parentConfigurator); } final public function __invoke($name, $path) { return $this->add($name, $path); } } 