 namespace Symfony\Component\VarDumper\Caster; use Symfony\Component\VarDumper\Cloner\Stub; @trigger_error('The '.__NAMESPACE__.'\MongoCaster class is deprecated since Symfony 3.4 and will be removed in 4.0.', E_USER_DEPRECATED); class MongoCaster { public static function castCursor(\MongoCursorInterface $cursor, array $a, Stub $stub, $isNested) { if ($info = $cursor->info()) { foreach ($info as $k => $v) { $a[Caster::PREFIX_VIRTUAL.$k] = $v; } } $a[Caster::PREFIX_VIRTUAL.'dead'] = $cursor->dead(); return $a; } } 